var npcID = 106919;
var hotspots = new List<Vector3>()
{
	new Vector3(504.791, 3952.093, 1.493344),
	new Vector3(554.4365, 3891.466, 5.685683),
	new Vector3(668.2935, 3927.591, 5.685683),
	new Vector3(779.6024, 3992.183, 5.773875),
};
var targ = ObjectManager.Target;
if (targ != null && targ.IsValid)
{
	if (targ.Entry != npcID)
	{
		Logging.WriteDebug("[Special Delivery] clear target");
		Lua.LuaDoString("ClearTarget();");
		return true;
	}
	if (targ.GetDistance > 5)
	{
		var targP = Questing.GetForwardPosition(targ.Position, targ.Rotation, 5);//  targ.Position;
		if (targP == null || targP == Vector3.Zero)
		{
			Logging.WriteDebug("[Special Delivery] unable to find forward position");
			return true;
		}

		Logging.WriteDebug("[Special Delivery] catch target");
		GoToTask.ToPosition(targP, 3.5f, false, (c) => {
			if (targ != null && targ.IsValid && targ.GetDistance < 30 && targ.Position.DistanceTo(targP) < 10)
			{
				Logging.WriteDebug("[Special Delivery] catch stop");
				return true;
			}

			return false;
		});
		return true;
	}
	Logging.WriteDebug("[Special Delivery] kill target");
	Interact.InteractGameObject(targ.GetBaseAddress);
	return true;
}
var npc = Questing.FindUnit(npcID);
if (npc != null && npc.IsValid && Questing.CheckGUID(npc.Guid, 2, 5))
{
	Logging.WriteDebug("[Special Delivery] choose target");
	Interact.InteractGameObject(npc.GetBaseAddress, true, false, true);
	return true;
}
var p = hotspots[Others.Random(0, hotspots.Count - 1)];
Logging.WriteDebug("[Special Delivery] search");
GoToTask.ToPosition(p, 10f, false, (c) => {
	var n = Questing.FindUnit(npcID);
	if (n != null && n.IsValid)
	{
		Logging.WriteDebug("[Special Delivery] found");
		Interact.InteractGameObject(n.GetBaseAddress, true, false, true);
		return false;
	}
	return true;
});
return true;

var vine = Questing.FindObject(250240, 250239);
if (vine != null && vine.IsValid && Questing.CheckGUID(vine.Guid, 1, 3) )
{
	var p = vine.Position;
	var dir = p - ObjectManager.Me.Position;
	dir /= dir.Magnitude();
	p -= (dir * 3);
	Questing.Gather(p, vine.Entry, 4f);
	return true;
}
GoToTask.ToPosition(new Vector3(573.0801, 3935.646, 5.68634, "None"));
return true;

var start = new Vector3(528.2327, 4086.119, 6.427363, "None");
