var npcID = 107318;
var minDist = 20f;
if (Questing.ObjectiveComplete(this, 2))
{
	if (ItemsManager.HasItemById(138147))
	{
		Logging.WriteDebug("[Shift Change] open gate");
		Questing.Gather(end, 250991, 5);
	}
	else if (ObjectManager.Me.Position.DistanceTo(end) > 20)
	{
		Logging.WriteDebug("[Shift Change] to end");
		GoToTask.ToPosition(end);
	}
	else
	{
		Questing.Attack(108616);
		Logging.WriteDebug("[Shift Change] attack npc");
	}
	return true;
}
var npc = Questing.FindUnit(npcID);
if (npc != null && npc.IsValid)
{
	if (ObjectManager.Me.PlayerUsingVehicle)
	{
		if (npc.GetDistance < minDist)
			return true;

		Logging.WriteDebug("[Shift Change] found npc. leave basket");
		Usefuls.EjectVehicle();
		return true;
	}
	Logging.WriteDebug("[Shift Change] escort npc");
	var dir = npc.Position - ObjectManager.Me.Position;
	dir /= dir.Magnitude();
	dir *= minDist;
	GoToTask.ToPosition(npc.Position - dir);
}
else if (!ObjectManager.Me.PlayerUsingVehicle)
{
	Logging.WriteDebug("[Shift Change] hide in basket");
	GoToTask.ToPositionAndIntecractWithNpc(start, 107473);
}
return true;

