public class FelsoulTeleporterOnline : QuestClass
{
	static int ancientManaCost = 100;
	public FelsoulTeleporterOnline()
	{
		Name = "Felsoul Teleporter Online!";
		QuestId.Add(41575);
		Step.AddRange(new[] { 100 });
	}
	public override bool IsComplete()
	{
		return SuramarQuests.AncientMana >= ancientManaCost;
	}
	public override bool PickUp()
	{
		if (SuramarQuests.GatherAncientMana.NotCollected(ancientManaCost))
			return true;

		return base.PickUp();
	}
	public override bool TurnIn()
	{
		if (SuramarQuests.GatherAncientMana.NotCollected(ancientManaCost))
			return true;

		return base.TurnIn();
	}
}

