var tree = ObjectManager.GetObjectWoWUnit().Where(u => u != null && u.IsValid && u.IsAlive && u.Entry == 111876 && u.GetDynamicFlags == 0).OrderBy(u => u.GetDistance).FirstOrDefault();
if (tree != null && tree.IsValid && tree.GetDynamicFlags == 0) // flag = 256 - already used
{
	if (tree.GetDistance2D < 5)
	{
		Logging.WriteDebug("[Wisp in the Willows] grow tree");
		MovementManager.StopMove();
		Thread.Sleep(Others.Random(300, 500));
		Questing.VehicleButton(1);
		Usefuls.WaitIsCasting();
		Thread.Sleep(Others.Random(1000, 2000));
		wManager.wManagerSetting.AddBlackList(tree.Guid, 3 * 60 * 1000);
		return true;
	}
	if (tree.GetDistance2D < 10)
	{
		Logging.WriteDebug("[Wisp in the Willows] try to reach tree");
		Questing.ClickMove(tree.Position, 5f);
		Thread.Sleep(Others.Random(1000, 2000));
		return true;
	}
	Logging.WriteDebug("[Wisp in the Willows] move close to tree");
	var movePath = Questing.PathClampDirected(path, ObjectManager.Me.Position, tree.Position);
	Questing.PathFromNear(movePath, true, () => ObjectManager.Me.PlayerUsingVehicle);
	return true;
}
Logging.WriteDebug("[Wisp in the Willows] move to end");
Questing.PathFromNear(path, true, () => {
	var t = ObjectManager.GetObjectWoWUnit().Where(u => u != null && u.IsValid && u.IsAlive && u.Entry == 111876 && u.GetDynamicFlags == 0).OrderBy(u => u.GetDistance).FirstOrDefault();
	if (t != null && t.IsValid)
	{
		Logging.WriteDebug("[Wisp in the Willows] found better tree");
	}
	return true;
});
return true;

