public class ToOldFriendsExtra : QuestClass
{
	static Thread _thread;
	public ToOldFriendsExtra()
	{
		Name = "To Old Friends (Extra)";
		QuestId.Add(38582);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public static void StartExtraThread()
	{
		if (_thread != null)
			StopExtraThread();

		_thread = new Thread(ThreadRoutine);
		_thread.Start();
	}
	public static void StopExtraThread()
	{
		if (_thread == null)
			return;

		_thread.Abort();
		_thread = null;
	}
	static void ThreadRoutine()
	{
		while (robotManager.Products.Products.IsStarted && Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
		{
			var targ = ObjectManager.Target;
			if (ObjectManager.Me.IsOutdoors && targ != null && targ.IsValid && targ.IsAlive && targ.GetDistance < 50)
			{
				if (Questing.UseExtraButton())
				{
					Thread.Sleep(Usefuls.Latency);
					ClickOnTerrain.Pulse(targ.Position);
					Thread.Sleep(Others.Random(5000, 6000));
				}
			}
			Thread.Sleep(500);
		}
	}
}
