public class SofteningtheTargetTurnIn : QuestClass
{
	public SofteningtheTargetTurnIn()
	{
		Name = "Softening the Target (Turn In)";
		QuestId.Add(43702);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.Complete(this);
	}
	public override bool Pulse()
	{
		var p = new Vector3(3413.7, 6124.011, 211.7968, "None");
		Questing.TurnIn(p, 104728);
		return true;
	}
}

