public class CloseEnoughtoTouchPickUp : QuestClass
{
	public CloseEnoughtoTouchPickUp()
	{
		Name = "Close Enough to Touch (Pick Up)";
		QuestId.Add(38687);
		QuestId.Add(41763);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsComplete()
	{
		return Questing.Recieved(this);
	}
	public override bool Pulse()
	{
		var p = new Vector3(3413.7, 6124.011, 211.7968, "None");
		Questing.PickUp(p, 104728);
		return true;
	}
}

if (!Questing.ObjectiveComplete(this, 2))
{
	Logging.WriteDebug("[Close Enough to Touch] obj #2");
	var p = new Vector3(3609.905, 6132.729, 178.1244);
	if (GoToTask.ToPosition(p))
	{
		var mob = Questing.FindUnit(111258, 111203);
		if (mob != null && mob.IsValid)
			GoToTask.ToPositionAndIntecractWithNpc(mob.Position, mob.Entry);
	}
	return true;
}
if (!Questing.ObjectiveComplete(this, 3))
{
	Logging.WriteDebug("[Close Enough to Touch] obj #3");
	var p = new Vector3(3746.8, 6165.945, 183.9481);
	if (GoToTask.ToPosition(p))
	{
		var mob = Questing.FindUnit(111260, 111198);
		if (mob != null && mob.IsValid)
			GoToTask.ToPositionAndIntecractWithNpc(mob.Position, mob.Entry);
	}
	return true;
}
if (!Questing.ObjectiveComplete(this, 4))
{
	Logging.WriteDebug("[Close Enough to Touch] obj #4");
	var p = new Vector3(3769.656, 6349.799, 183.9394);
	if (GoToTask.ToPosition(p))
	{
		var mob = Questing.FindUnitIncludeDead(111259, 111204);
		if (mob != null && mob.IsValid)
			GoToTask.ToPositionAndIntecractWithNpc(mob.Position, mob.Entry, 1, false, null, true);
	}
	return true;
}
if (!Questing.ObjectiveComplete(this, 5))
{
	Logging.WriteDebug("[Close Enough to Touch] obj #5");
	var p = new Vector3(3576.754, 6388.25, 167.5658);
	if (GoToTask.ToPosition(p))
		Questing.CancelCutscene();

	return true;
}
return true;

