public class TheFateofValsharah : QuestClass
{
	public TheFateofValsharah()
	{
		Name = "The Fate of Val'sharah";
		QuestId.Add(38743);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 2);
	}
	public override bool Pulse()
	{
		if (ObjectManager.Me.IsOnTaxi || ObjectManager.Me.PlayerUsingVehicle)
			return true;

		if (!Questing.ObjectiveComplete(this, 1))
		{
			var p = new Vector3(3547.351, 6420.47, 168.3023, "None");
			if (GoToTask.ToPositionAndIntecractWithNpc(p, 104799, 1))
			{

			}
			return true;
		}

		var mob = Questing.FindUnit(93065);
		if (Questing.Attack(mob))
			return true;

		var center = new Vector3(2912.206, 6647.792, 215.0921);
		GoToTask.ToPosition(center);
		return true;
	}
}
