public class TheFarmsteads : QuestClass
{
	static Thread _thread;
	static Vector3 _HeathrowHouseBasementCenter = new Vector3(2562.455, 7498.951, 20.0503, "None");
	static Vector3 _HeathrowHouseBasementP = new Vector3(2565.066, 7486.539, 29.5022, "None");
	static Vector3 _HeathrowHouseBasementOutside = new Vector3(2580.021, 7488.425, 28.02943, "None");
	static Vector3 _HeathrowHouseBasementInside = new Vector3(2556.758, 7490.302, 20.0503, "None");
	static int _HeathrowHouseBasementDoor = 243007;
	public TheFarmsteads()
	{
		Name = "The Farmsteads";
		QuestId.Add(38644);
		Step.AddRange(new[] { 1 });
	}
	static void Log(string text)
	{
		Logging.WriteDebug("[The Farmsteads] " + text);
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1) && Questing.ObjectiveComplete(this, 2) && Questing.ObjectiveComplete(this, 3);
	}
	public static bool InHeathrowHouse
	{
		get
		{
			return ObjectManager.Me.Position.DistanceTo2D(_HeathrowHouseBasementCenter) < 50 && ObjectManager.Me.IsIndoors;
		}
	}
	static bool OpenBasement()
	{
		return Questing.Gather(_HeathrowHouseBasementP, _HeathrowHouseBasementDoor);
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			var p = new Vector3(2571.091, 7506.483, 19.94402);
			if (!InHeathrowHouse)
			{
				Log("#1 - Heathrow house. into basement");
				if (OpenBasement())
					GoToTask.ToPosition(_HeathrowHouseBasementInside);

				return true;
			}
			Log("#1 - Heathrow house");
			GoToTask.ToPositionAndIntecractWithNpc(p, 92734, 1);
			return true;
		}
		if (InHeathrowHouse)
		{
			Log("In Heathrow house. out from basement");
			if (OpenBasement())
				GoToTask.ToPosition(_HeathrowHouseBasementOutside);

			return true;
		}
		if (!Questing.ObjectiveComplete(this, 2))
		{
			Log("#2 - Corn Fields");
			var p = new Vector3(2707.609, 7462.568, 5.313876);
			if (GoToTask.ToPositionAndIntecractWithNpc(p, 92618, 1))
			{
			}
			return true;
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
			Log("#3 Fishing Dock");
			var p = new Vector3(2817.782, 7561.732, 4.199298);
			var pRandom = robotManager.Helpful.Math.GetRandomPointInCircle(p, 10);
			if (GoToTask.ToPosition(pRandom))
			{
			}
			return true;
		}
		return true;
	}
	public static void StartDoorThread()
	{
		if (_thread != null)
			StopDoorThread();

		_thread = new Thread(ThreadRoutine);
		_thread.Start();
		Log("door thread started");
	}
	public static void StopDoorThread()
	{
		if (_thread == null)
			return;

		_thread.Abort();
		_thread = null;
		Log("door thread stopped");
	}
	static void ThreadRoutine()
	{
		while (robotManager.Products.Products.IsStarted && Conditions.InGameAndConnectedAndAliveAndProductStartedNotInPause)
		{
			var door = ObjectManager.GetObjectWoWGameObject().FirstOrDefault(o => o.GOType == WoWGameObjectType.Door
				&& o.GetDistance < 7
				//&& o.CanOpen
				&& !wManager.wManagerSetting.IsBlackListed(o.Guid)
			);
			//Log("door > " + door);
			if (door != null && door.IsValid)
			{
				Questing.MovementDisabled = true;
				Interact.InteractGameObject(door.GetBaseAddress);
				Usefuls.WaitIsCasting();
				//Questing.Gather(door.Position, door.Entry);
				wManager.wManagerSetting.AddBlackList(door.Guid, 5 * 1000);
				Thread.Sleep(300);
				Questing.MovementDisabled = false;
			}
			Thread.Sleep(100);
		}
	}
	static void TestDoors()
	{
		var a = ObjectManager.GetObjectWoWGameObject().Where(o => o.GOType == WoWGameObjectType.Door);
		foreach (var b in a)
		{
			Logging.Write(">>> FOUND " + b.Name + " GOType=" + b.GOType + " GetDistance=" + b.GetDistance + " CanOpen=" + b.CanOpen + " blacklisted=" + wManager.wManagerSetting.IsBlackListed(b.Guid) + " ");

		}
	}
}

