public class KurtalosRavencrest : QuestClass
{
	public KurtalosRavencrest()
	{
		Name = "Kur'talos Ravencrest";
		QuestId.Add(38718);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.ObjectiveComplete(this, 1);
	}
	public override bool Pulse()
	{
		var p = new Vector3(3017.747, 7353.211, 37.82928, "None");
		if (ObjectManager.Me.Position.DistanceTo(p) > 10)
		{
			return GoToTask.ToPosition(p);
		}
		Questing.ClickMove(p, 1f);
		Thread.Sleep(Others.Random(2000, 3000));
		var banner = Questing.FindObject(243089);
		if (banner != null && banner.IsValid && banner.GetDistance < 6)
		{
			Interact.InteractGameObject(banner.GetBaseAddress);
		}
		return true;
	}
}

