public class IllidariFreedom : QuestClass
{
	public IllidariFreedom()
	{
		Name = "Illidari Freedom";
		QuestId.Add(44278);
		QuestId.Add(38719);
		Step.AddRange(new[] { 1, 1, 1, 0, 0 });
	}
	public override bool Pulse()
	{
		if (!Questing.ObjectiveComplete(this, 1))
		{
			var p = new Vector3(3367.568, 7408.228, 16.82367);
			return GoToTask.ToPositionAndIntecractWithNpc(p, 94974, 1);
		}
		if (!Questing.ObjectiveComplete(this, 3))
		{
