public class TheTearsofElune : QuestClass
{
	public TheTearsofElune()
	{
		Name = "The Tears of Elune";
		QuestId.Add(40890);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Questing.Complete(this);
	}
	public override bool Pulse()
	{
		if (!LegionQuests.InDalaraPortraitRoom)
		{
			LegionQuests.ToDalaranPortraitRoom();
			return true;
		}
		var p = new Vector3(-857.5178, 4466.536, 588.8563, "None");
		if (Questing.Gather(p, 246466, 7))
		{
			Thread.Sleep(Others.Random(1000, 2000));
			Quest.CompleteQuest(1);
		}
		return true;
	}
}

