public class FromDalaranPortraitRoom : QuestClass
{
	public FromDalaranPortraitRoom()
	{
		Name = "From Dalaran Portrait Room";
		QuestId.Add(0);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsComplete()
	{
		return !LegionQuests.InDalaraPortraitRoom;
	}
	public override bool IsCompleted()
	{
		return IsComplete();
	}
	public override bool Pulse()
	{
		LegionQuests.FromDalaranPortraitRoom();
		return true;
	}
}
