	public static class Instances
	{
		public static bool InBlackTemple
		{
			get
			{
				return Usefuls.ContinentId == (int) ContinentId.BlackTemple;
			}
		}

		public static void ToBlackTemple()
		{
			if (InBlackTemple)
			{
				TravelLog("Im in Black Temple");
			}
			else if (NearBlackTemple)
			{
				TravelLog("Im near Black Temple, going inside");
				GoToTask.ToPosition(Coords.BLACKTEMPLE_MOVEIN);
			}
			else
			{
				ToBlackTempleNear();
			}
		}

		public static bool NearBlackTemple
		{
			get
			{
				if (InBlackTemple)
					return true;
				if (!InOutlands)
					return false;

				return ObjectManager.Me.Position.DistanceTo2D(Coords.BLACKTEMPLE_NEAR) < 500;
			}
		}

		public static void ToBlackTempleNear()
		{
			uint teleportMedalion = 32757;  //http://www.wowhead.com/item=32757/blessed-medallion-of-karabor
			if (InBlackTemple || NearBlackTemple)
			{
				TravelLog("Im in Black Temple or near");
			}
			else if (ItemsManager.HasItemById(teleportMedalion))
			{
				TravelLog("Equipping teleport item");
				ItemsManager.EquipItemByName(ItemsManager.GetNameById(teleportMedalion));
				Thread.Sleep(35 * 1000);
				TravelLog("Teleporting to Black Temple");
				ItemsManager.UseItem(teleportMedalion);
				Usefuls.WaitIsCasting();
			}
			else if (InOutlands)
			{
				TravelLog("Im in Outlands. Moving near Black Temple");
				GoToTask.ToPosition(Coords.BLACKTEMPLE_NEAR);
			}
			else
			{
				TravelLog("Im in somewhere else. Go to Shattrath");
				ToShattrath();
				Thread.Sleep(10 * 1000);
				ToBlackTempleNear();
			}
		}

		// sunwell
		public static bool InSunwell
		{
			get
			{
				return Usefuls.ContinentId == (int)ContinentId.SunwellPlateau;
			}
		}

		public static void ToSunwell()
		{
			if (InSunwell)
			{
				TravelLog("Im in Sunwell");
			}
			else if (InQuelDanas)
			{
				TravelLog("Im in Quel Danas, goin to sunwell");
				GoToTask.ToPosition(Coords.SUNWELLPLATEAU_MOVEIN);
			}
			else
			{
				ToQuelDanas();
			}
		}

	}

	public enum AreaId
	{
		QuelDanas = 4080,
		Shattrath = 3703,
		DalaranNorthrend = 4395,
		DalaranBrokenIsles = 7502,
		PandariaShrineAlliance = 6553, //alliance PandariaShrineOfSevenStars
		PandariaShrineAllianceNear = 6142,
		PandariaShrineHorde = 6141, //horde PandariaShrineOfTwoMoons
		PandariaShrineHordeNear = 6554,
	}

	public class PortalInfo
	{
		public Vector3 position;
		public int objectId;
		public PortalInfo(Vector3 pos, int id)
		{
			position = pos;
			objectId = id;
		}

		public void Use()
		{
			GoToTask.ToPosition(position);
			MountTask.DismountMount();
			GoToTask.ToPositionAndIntecractWithGameObject(position, objectId);
		}
	}

	static void TravelLog(string message)
	{
		Logging.WriteDebug("[Traveler] " + message);
	}

	static void TravelError(string message)
	{
		Logging.WriteError("[Traveler] " + message);
	}

	public Traveler()
	{
		Name = "Traveler";
		SetDefaultSettings();
	}

	static Traveler()
	{
	}

	public static void SetDefaultSettings()
	{
		var config = wManager.wManagerSetting.CurrentSetting;
		config.CloseIfPlayerTeleported = false;
		config.MaxUnitsNear = 99;
		config.CanAttackUnitsAlreadyInFight = true;
		config.AttackElite = true;
		config.AttackBeforeBeingAttacked = false;
		config.SearchRadius = 85;
		config.SkinNinja = false;
		config.SkinMobs = false;
		config.HarvestHerbs = false;
		config.HarvestMinerals = false;
		config.HarvestTimber = false;
		config.UseFlyingMount = true;
		config.UseGroundMount = true;
		config.UseMount = true;
		config.IgnoreFightGoundMount = true;
		wManager.Wow.Helpers.CVar.SetCVar("autoDismount", "1");
		wManager.Wow.Helpers.CVar.SetCVar("autoDismountFlying", "1");
		wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = false;
		Logging.Write("set default settings");
	}

}

public sealed class GoingBearback : QuestClass
{
	public GoingBearback()
	{
		Name = "Going Bearback";
		QuestId.Add(12851);
		Step.AddRange(new[] { 15, 7, 0, 0, 0 });
	}

	private bool _isComplete;

	public override bool IsComplete() // Override iscomplete for add _step1
	{
		if (IsCompleted()) // If quest already turnin
			return true;

		if (!HasQuest()) // If character not have this quest
			return false;

		if (!_isComplete)
			_isComplete = (Quest.IsObjectiveComplete(1, QuestId[0]) && Quest.IsObjectiveComplete(2, QuestId[0]));

		return _isComplete;
	}

	public override bool Pulse()
	{
/*/
		if (IsComplete())
		{
			//Logging.Write("no more todo in quest");
			wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = false;
			wManager.Wow.Bot.Tasks.MountTask.DismountMount();
			wManager.Wow.Helpers.Usefuls.EjectVehicle();
			return true;
		}
//*/
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			//Logging.Write("no vehicle");
			//mount bear //
			wManager.Wow.Bot.Tasks.GoToTask.ToPositionAndIntecractWithNpc(new Vector3(7086.768, -1937.103, 773.5932), 29598, -1);
		}

		// ignore fight //
		wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = true;

		float spellDistance = 79f;
		int mob1Id = 29358; //worg
		int mob2Id = 29351; //giant

		//worg
		if (!Quest.IsObjectiveComplete(1, QuestId[0]))
		{
			var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mob1Id));
			if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
			{
				if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < spellDistance)
				{
					//Logging.Write("use vehicle");
					//wManager.Wow.Helpers.ClickOnTerrain.Spell(spellId, mobEntry.Position, true, false);
					wManager.Wow.Helpers.Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
					//Thread.Sleep(150);
					wManager.Wow.Helpers.ClickOnTerrain.Pulse(mobEntry.Position);
					wManager.Wow.Helpers.Usefuls.WaitIsCasting();
				}
			}
		}
		//worg
		if (!Quest.IsObjectiveComplete(2, QuestId[0]))
		{
			var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mob2Id));
			if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
			{
				if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < spellDistance)
				{
					//Logging.Write("use vehicle");
					//wManager.Wow.Helpers.ClickOnTerrain.Spell(spellId, mobEntry.Position, true, false);
					wManager.Wow.Helpers.Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
					//Thread.Sleep(150);
					wManager.Wow.Helpers.ClickOnTerrain.Pulse(mobEntry.Position);
					wManager.Wow.Helpers.Usefuls.WaitIsCasting();
				}
			}
		}
		return false;
	}
}

public sealed class ColdHearted : QuestClass
{
	public ColdHearted()
	{
		Name = "Cold Hearted";
		QuestId.Add(12856);
		Step.AddRange(new[] { 9, 3, 0, 0, 0 });
	}

	private int _rescued;
	private bool _isComplete;
	private robotManager.Helpful.Timer _timer;

	public override bool IsComplete() // Override iscomplete for add _step1
	{
		if (IsCompleted()) // If quest already turnin
			return true;

		if (!HasQuest()) // If character not have this quest
			return false;

		if (!_isComplete)
			_isComplete = (Quest.IsObjectiveComplete(1, QuestId[0]) && Quest.IsObjectiveComplete(2, QuestId[0]));

		return _isComplete;
	}

	private bool _debug = false;
	void DebugLog(string msg)
	{
		if (_debug)
			Logging.Write(msg);
	}

	public override bool Pulse()
	{
		//mount dragon
		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			//Logging.Write("Go to dragon");
			_rescued = 0;
			// enable mount and fight
			wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
			wManager.wManagerSetting.CurrentSetting.UseFlyingMount = true;
			wManager.wManagerSetting.CurrentSetting.UseGroundMount = true;
			wManager.wManagerSetting.CurrentSetting.UseMount = true;
			wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = false;
			wManager.Wow.Bot.Tasks.GoToTask.ToPosition(new Vector3(7329.138, -2710.707, 779.6351, "Flying")); // center
			wManager.Wow.Bot.Tasks.GoToTask.ToPositionAndIntecractWithNpc(new Vector3(7212.301, -2655.96, 866.796, "Flying"), 29708, -1);
			// disable mount and fight
			wManager.wManagerSetting.CurrentSetting.UseFlyingMount = false;
			wManager.wManagerSetting.CurrentSetting.UseGroundMount = false;
			wManager.wManagerSetting.CurrentSetting.UseMount = false;
			wManager.Wow.Helpers.Conditions.ForceIgnoreIsAttacked = true;
			wManager.Wow.Bot.Tasks.GoToTask.ToPosition(new Vector3(7329.138, -2710.707, 779.6351, "Flying")); // center
		}

		//rescued, need to get dragon back
		if (_rescued >= 3)
		{
			DebugLog("Go to outside. rescued 3");
			wManager.Wow.Bot.Tasks.GoToTask.ToPosition(new Vector3(7329.138, -2710.707, 779.6351, "Flying")); // center
			wManager.Wow.Bot.Tasks.GoToTask.ToPosition(new Vector3(7300.828, -2482.212, 814.2418, "Flying"));
		}

		//rescue
		if (!Quest.IsObjectiveComplete(1, QuestId[0]) || !Quest.IsObjectiveComplete(2, QuestId[0]))
		{
			int mobId = 29639; //Brunnhildar Prisoners
			var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobId));
			if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsValid)
			{
				//if can't reach mob for 30sec, blacklist for 10min
				if (_timer == null || _timer.IsReady)
				{
					wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 10 * 60);
					_timer = new robotManager.Helpful.Timer(1000 * 30);
					DebugLog("Cant reach target. blacklisting");
				}

				if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < 15f)
				{
					// Target mob
					if (!mobEntry.IsMyTarget)
					{
						Interact.InteractGameObject(mobEntry.GetBaseAddress);
						Thread.Sleep(Usefuls.Latency + 150);
					}
					MovementManager.Face(ObjectManager.Target);
					Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
					Usefuls.WaitIsCasting();
					wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 5 * 60);
					Lua.RunMacroText("/cleartarget");
					DebugLog("Targeting and casting. rescued=" + _rescued);
					_rescued += 1;
				}
				else
				{
					var hoverPosition = mobEntry.Position.InFrontOf(0, 3);
					//hoverPosition.Z += 3f;
					hoverPosition.Type = "Flying";
					DebugLog("Target far. goto=" + hoverPosition);
					wManager.Wow.Bot.Tasks.GoToTask.ToPosition(hoverPosition);
				}
			}
		}
		return true;
	}

}

public sealed class TheWarmUp : QuestClass
{
	public TheWarmUp()
	{
		Name = "The Warm-Up";
		QuestId.Add(12996);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}

	public override bool IsComplete() // Override iscomplete for add _step1
	{
		if (IsCompleted()) // If quest already turnin
			return true;

		if (!HasQuest()) // If character not have this quest
			return false;

		return Quest.IsObjectiveComplete(1, QuestId[0]);
	}

	public override bool Pulse()
	{
		float spellDistance = 9.5f;
		int mobId = 29352; // kirgaraak (yeti)
		uint itemId = 42481; //Reins of the Warbear Matriarch

		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			DebugLog("go to yeti");

			// enable mount and fight
			wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
			wManager.wManagerSetting.CurrentSetting.UseFlyingMount = true;
			wManager.wManagerSetting.CurrentSetting.UseGroundMount = true;
			wManager.wManagerSetting.CurrentSetting.UseMount = true;
			Conditions.ForceIgnoreIsAttacked = false;

			wManager.Wow.Bot.Tasks.GoToTask.ToPosition(new Vector3(6992.99, -1769.67, 825.3571, "None"));
			Thread.Sleep(Usefuls.Latency + 150);

			DebugLog("mount bear");
			// disable mount and fight
			wManager.wManagerSetting.CurrentSetting.UseFlyingMount = false;
			wManager.wManagerSetting.CurrentSetting.UseGroundMount = false;
			wManager.wManagerSetting.CurrentSetting.UseMount = false;
			Conditions.ForceIgnoreIsAttacked = true;

			ItemsManager.UseItem(itemId);
			Usefuls.WaitIsCasting();
		}

		if (!Quest.IsObjectiveComplete(1, QuestId[0]))
		{
			var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobId));
			if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
			{
				if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < spellDistance)
				{
					// Target mob
					if (!mobEntry.IsMyTarget)
					{
						Interact.InteractGameObject(mobEntry.GetBaseAddress);
						Thread.Sleep(Usefuls.Latency + 150);
					}
					MovementManager.Face(ObjectManager.Target);
					DebugLog("use vehicle on yeti");
					Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton4");
					Thread.Sleep(2 * 1000 + Others.Random(50, 100) );
				}
				else
				{
					var nearPosition = mobEntry.Position.InFrontOf(0, 3);
					DebugLog("Target far. goto=" + nearPosition);
					wManager.Wow.Bot.Tasks.GoToTask.ToPosition(nearPosition);
				}
			}
		}
		return true;
	}

	private bool _debug = false;
	void DebugLog(string msg)
	{
		if (_debug)
			Logging.Write(msg);
	}

}

