public sealed class TheDrakkensryd : QuestClass
{
	public TheDrakkensryd()
	{
		Name = "The Drakkensryd";
		QuestId.Add(12886);
		Step.AddRange(new[] { 10, 0, 0, 0, 0 });
	}

	private bool _isComplete = false;

	public override bool IsComplete() // Override iscomplete for add _step1
	{
		if (IsCompleted()) // If quest already turnin
			return true;

		if (!HasQuest()) // If character not have this quest
			return false;

		return _isComplete;// Quest.IsObjectiveComplete(1, QuestId[0]);
	}

	public override bool Pulse()
	{
		float attackDistance = 5f;
		int columnId = 29754;
		int protoDrakeId = 29625;
		int drakeRiderId = 29694;

		if (!ObjectManager.Me.PlayerUsingVehicle)
		{
			if (Quest.IsObjectiveComplete(1, QuestId[0]))
			{
				_isComplete = true;
				return true;
			}
			DebugLog("not on drake");
			return false;
		}
		// disable mount and fight
		wManager.wManagerSetting.CurrentSetting.UseFlyingMount = false;
		wManager.wManagerSetting.CurrentSetting.UseGroundMount = false;
		wManager.wManagerSetting.CurrentSetting.UseMount = false;
		Conditions.ForceIgnoreIsAttacked = true;

		if (Quest.IsObjectiveComplete(1, QuestId[0]))
		{
			if (TryUseHarpoon(columnId))
			{
				DebugLog("riders killed, quest complete. harpooning column");
				_isComplete = true;
				return true;
			}
		}

		var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(drakeRiderId));
		if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
		{
			if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < attackDistance)
			{
				// Target mob
				if (!mobEntry.IsMyTarget)
				{
					Interact.InteractGameObject(mobEntry.GetBaseAddress);
					Thread.Sleep(Usefuls.Latency + 150);
				}
				DebugLog("Attacking drakerider");
				Conditions.ForceIgnoreIsAttacked = false;
				//Fight.StartFight(mobEntry.Guid);
				Fight.StopFight();
				Lua.RunMacroText("/startattack"); //figthclass dont work on vehicle
				return true;
			}
			DebugLog("drakerider too far");
		}

		TryUseHarpoon(protoDrakeId);
		return true;
	}

	private bool TryUseHarpoon(int mobId)
	{
		var harpoonRangeMin = 10f;
		var harpoonRangeMax = 99f;
		uint harpoonId = 41058;
		int harpoonSpellId = 54933;
		var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobId));
		if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsValid)
		{
			if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < harpoonRangeMax)
			{
				if (mobEntry.Position.DistanceTo(ObjectManager.Me.Position) < harpoonRangeMin)
				{
					wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 10 * 60);
					DebugLog("failed harpoon. too close. blacklisting");
					Lua.RunMacroText("/cleartarget");
					return false;
				}
				// Target mob
				if (!mobEntry.IsMyTarget)
				{
					Interact.InteractGameObject(mobEntry.GetBaseAddress);
					Thread.Sleep(Usefuls.Latency + 150);
				}
				DebugLog("using harpoon");
				ItemsManager.UseItem(harpoonId);
				Thread.Sleep(5 * 1000 + Others.Random(50, 100));
				return true;
			}
		}
		DebugLog("failed harpoon");
		return false;
	}


	private bool _debug = true;
	void DebugLog(string msg)
	{
		if (_debug)
			Logging.Write(msg);
	}



}

wManager.wManagerSetting.CurrentSetting.SearchRadius = 200;
var eggObjectId = 191840;
uint vialItemId = 41612;
var valkyrionAspirantId = 29569;
var startPosition = new Vector3(7433.672, 201.9405, 768.3013, "None");
if (ObjectManager.Me.Position.DistanceTo(startPosition) > 200)
{
	//Logging.Write("go to start");
	GoToTask.ToPosition(startPosition);
	return true;
}

if (ItemsManager.HasItemById(vialItemId))
{
	var eggEntry = ObjectManager.GetNearestWoWGameObject(ObjectManager.GetWoWGameObjectByEntry(eggObjectId));
	if (eggEntry != null && eggEntry.IsValid)
	{
		if (eggEntry.GetDistance < 20)
		{
			//Logging.Write("use vial on eggs");
			ItemsManager.UseItem(vialItemId);
			Thread.Sleep(Usefuls.Latency * 2);
			wManager.Wow.Helpers.ClickOnTerrain.Pulse(eggEntry.Position);
			wManager.Wow.Helpers.Usefuls.WaitIsCasting();
			Thread.Sleep(Usefuls.Latency * 2);
		}
		else
		{
			//Logging.Write("go to egg");
			GoToTask.ToPosition(eggEntry.Position);
		}
	}
	else
	{
		//Logging.Write("no eggs. go to start");
		GoToTask.ToPosition(startPosition);
	}
	return true;
}
var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(valkyrionAspirantId));
if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsAttackable && mobEntry.IsValid)
{
	//Logging.Write("get vial");
	Fight.StartFight(mobEntry.Guid);
}
else
{
	var randomPoints = new List<Vector3>() {
		new Vector3(7367.067, 263.7455, 771.2306, "None"),
		new Vector3(7357.921, 158.548, 781.2589, "None"),
		new Vector3(7391.188, 30.7574, 772.3488, "None"),
	};
	var randomPointIndex = robotManager.Helpful.Others.Random(0, randomPoints.Count - 1);
	var randomPoint = randomPoints[randomPointIndex];
	//Logging.Write("get vial. farm on points");
	GoToTask.ToPosition(randomPoint);
}
return true;
