public class MoveOutsideSmallCave : QuestFollowPathClass
{
	public MoveOutsideSmallCave() : base()
	{
		Name = "Move Outside Small Cave";
		QuestId.Add(0);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
		Path = new List<Vector3>() {
			new Vector3(875.8203f, 2579.39f, -56.82841f, "None"),
			new Vector3(870.4339f, 2574.925f, -56.78955f, "None"),
			new Vector3(867.6859f, 2571.939f, -56.73555f, "None"),
			new Vector3(865.2072f, 2568.752f, -56.7318f, "None"),
			new Vector3(862.8126f, 2565.575f, -56.04571f, "None"),
			new Vector3(860.0367f, 2562.673f, -55.75048f, "None"),
			new Vector3(856.8422f, 2560.178f, -55.92456f, "None"),
			new Vector3(853.7421f, 2557.693f, -55.98026f, "None"),
			new Vector3(850.6247f, 2555.127f, -55.98026f, "None"),
		};
	}

	public override bool IsComplete()
	{
		var pathStart = Path[0];
		var pathEnd = Path[Path.Count - 2];
		var maxDist = pathStart.DistanceTo(pathEnd);
		var result = false;
		result = result || !ObjectManager.Me.IsIndoors;
		result = result || ObjectManager.Me.Position.DistanceTo(pathStart) > maxDist;
		return result;
	}

	public override bool CanConditions()
	{
		return !IsComplete();
	}
	public override bool IsCompleted()
	{
		return false;
	}
	public override bool HasQuest()
	{
		return true;
	}

}
