QuestClass.Code:
// FOOD & REPAIR // http://www.wowhead.com/npc=96650/falara-nightsong
var npc = new Npc
{
	Entry = 96650,
	Position = new Vector3(1426.901, 1710.019, 54.46369, "None"),
	Type = Npc.NpcType.Vendor
};
uint itemId = 132752; // http://www.wowhead.com/item=132752/illidari-rations

var needCount = 20;
var needToBuy = needCount - ItemsManager.GetItemCountById(itemId);
//var needToBuy = needCount - ItemsManager.GetItemCountByIdLUA(itemId);
//var needToBuy = needCount - ItemsManager.GetItemCountByNameLUA(ItemsManager.GetNameById(itemId));

if (ObjectManager.Me.Position.DistanceTo(npc.Position) < 90)
{
	if (needToBuy > 0)
		Questing.Buy(npc.Position, npc.Entry, (int)itemId, needToBuy);

	if (ObjectManager.Me.GetDurabilityPercent < 100)
		Questing.Repair(npc.Position, npc.Entry);
}
RequiredQuest = -1;
return true;

