	public override bool IsComplete()
	{
		if (IsCompleted())
			return true;

		if (!HasQuest())
			return false;

		if (ObjectManager.Me.Position.DistanceTo(_centerPoint) < Radius || ObjectManager.Me.Position.Y < _rightPoint.Y)
			return true;

		return false;
	}

