uint spellId = 188501; //http://www.wowhead.com/spell=188501/spectral-sight
var rockId = 245045;
var step1Position = new Vector3(1262.847, 1614.565, 104.7983, "None");
var step2Position = new Vector3(1249.515, 1629.147, 101.2244, "None");
if (GoToTask.ToPosition(step1Position))
{
	if (GoToTask.ToPosition(step2Position))
	{
		var rock = ObjectManager.GetObjectWoWGameObject().Where( o => o.IsValid && o.Entry == rockId).OrderBy(o => o.GetDistance).FirstOrDefault();
		if (rock != null)
		{
			MovementManager.Face(rock.Position);
			Thread.Sleep(500);
			SpellManager.CastSpellByIdLUA(spellId);
			Thread.Sleep(2 * 1000);
		}
	}
}
return true;

