if (GoToTask.ToPositionAndIntecractWithGameObject(tomePosition, tomeId))
{
	Thread.Sleep(3 * 1000);
	if (robotManager.Helpful.Var.Exist("DemonHunter_Havoc") && robotManager.Helpful.Var.GetVar<bool>("DemonHunter_Havoc"))
	{
		wManager.Wow.Helpers.Lua.LuaDoString("QuestChoiceFrameOption1.OptionButton:Click();");
		Logging.Write("Choosed Havoc");
	}
	else
	{
		wManager.Wow.Helpers.Lua.LuaDoString("QuestChoiceFrameOption2.OptionButton:Click();");
		Logging.Write("Choosed Vengeance");
	}
	Thread.Sleep(3 * 1000);
}
return true;
