public sealed class AllTheWayUp : QuestClass
{
	float _radius = 5f;
	float _deltaZ = 3f;
	float _floorHeight = 50;
	int _elevatorId = 244644;
	int _waitTime = 2 * 1000;
	Vector3 _bottomPosition = new Vector3(4451.472, -449.4385, 118.8894, "None");
	Vector3 _upperPosition = new Vector3(4474.62, -453.6425, 253.5476, "None");

	public AllTheWayUp() // Don't forget to use good classe name
	{
		Name = "All The Way Up";
		QuestId.Add(39686);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 }); // Objective required count

		// More info here: http://wrobot.eu/topic/374-how-to-create-quest-profile/ (please don't add using and add only one quest)
	}

	public override bool Pulse()
	{
		// in elevator
		if (ObjectManager.Me.InTransport)
		{
			if (ObjectManager.Me.Position.Z > _upperPosition.Z - _deltaZ)
			{
				//Logging.Write("Goto upper point");
				Move.Forward(Move.MoveAction.DownKey, 5 * 1000);
				//GoToTask.ToPosition(_upperPosition);
			}
			else
			{
				//Logging.Write("Moving on elevator, wait");
				MovementManager.Face(_upperPosition);
				Thread.Sleep(_waitTime);
			}
		}
		// not in elevator
		else
		{
			//at lower floor
			if (ObjectManager.Me.Position.Z < _upperPosition.Z - _floorHeight)
			{
				//too far from evelator point
				if (ObjectManager.Me.Position.DistanceTo(_bottomPosition) > _radius)
				{
					//Logging.Write("Go to bottom elevator");
					GoToTask.ToPosition(_bottomPosition);
				}
				var elevator = ObjectManager.GetObjectWoWGameObject()
					.Where(o => o.IsValid && o.Entry == _elevatorId)
					.OrderBy(o => o.GetDistance).FirstOrDefault();
				if (elevator != null && elevator.Position.Z < _bottomPosition.Z + _deltaZ)
				{
					//Logging.Write("elevator near, jump");
					Move.JumpOrAscend();
					Thread.Sleep(_waitTime);
				}
				else
				{
					//Logging.Write("wait elevator");
					Thread.Sleep(_waitTime);
				}
			}
			else
			{
				GoToTask.ToPosition(_upperPosition);
				Thread.Sleep(_waitTime);
			}
		}
		return true;
	}

	public override bool IsComplete()
	{
		return ObjectManager.Me.Position.DistanceTo(_upperPosition) < _radius;
	}

}

