if (GoToTask.ToPositionAndIntecractWithNpc(npcPosition, npcId, 1))
{
	Thread.Sleep(3 * 1000);
	if (robotManager.Helpful.Var.Exist("DemonHunter_KaynSunfury") && robotManager.Helpful.Var.GetVar<bool>("DemonHunter_KaynSunfury"))
	{
		wManager.Wow.Helpers.Lua.LuaDoString("QuestChoiceFrameOption1.OptionButton:Click();");
		Logging.Write("Choosed Kayn Sunfury");
	}
	else
	{
		wManager.Wow.Helpers.Lua.LuaDoString("QuestChoiceFrameOption2.OptionButton:Click();");
		Logging.Write("Choosed Altruis the Sufferer");
	}
	Thread.Sleep(3 * 1000);
}
return true;

var npcPosition = new Vector3(4160.994, -855.743, 291.1075, "None");
var npcIds = new List<int>() {
	96666, // http://www.wowhead.com/npc=96666/kayn-sunfury
	96669, // http://www.wowhead.com/npc=96669/altruis-the-sufferer
};
if (ObjectManager.Me.Position.DistanceTo(npcPosition) > 5)
{
	GoToTask.ToPosition(npcPosition);
}
var npc = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(npcIds));
if (npc != null && npc.IsValid)
{
	if (GoToTask.ToPositionAndIntecractWithNpc(npc.Position, npc.Entry))
	{
		Lua.LuaDoString("GossipTitleButton1:Click();");
		Thread.Sleep(2 * 1000);
		Lua.LuaDoString("QuestFrameCompleteQuestButton:Click();");
		Thread.Sleep(2 * 1000);
	}
}
return true;
