// if victim near
var victims = new List<int>(){
	115460, //http://www.wowhead.com/npc=115460/hydra-drawing
	115461, //http://www.wowhead.com/npc=115461/harpy-drawing
	115462, //http://www.wowhead.com/npc=115462/squallhunter-drawing
};
var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(victims));
if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
{
	Interact.InteractGameObject(mob.GetBaseAddress);
	Fight.StartFight(mob.Guid);
	return true;
}

