else if (!WorldQuest.ObjectiveComplete(4, 44788))
{
	var step1 = new Vector3(183.8165, 6483.945, -52.86467, "None");
	var step2 = new Vector3(185.017, 6484.542, -51.15879, "None");
	var step3 = new Vector3(186.0768, 6484.852, -49.21067, "None");
	if (GoToTask.ToPosition(step1, 1.5f))
	{
		MovementManager.MoveTo(step1);
		Thread.Sleep(1 * 1000);

		MovementManager.MoveTo(step1);
		Move.JumpOrAscend();
		Thread.Sleep(1 * 1000);

		MovementManager.MoveTo(step2);
		Move.JumpOrAscend();
		Thread.Sleep(1 * 1000);

		MovementManager.MoveTo(step3);
		Move.JumpOrAscend();
		Thread.Sleep(1 * 1000);


		var bookObj = ObjectManager.GetObjectWoWGameObject().Where(o => o.IsValid && o.Entry == book2).OrderBy(o => o.GetDistance).FirstOrDefault();
		if (bookObj != null)
		{
			Interact.InteractGameObject(bookObj.GetBaseAddress);
			Thread.Sleep(3 * 1000);
		}
	}
}
