public class AConduitNoMoreInvasion : QuestClass
{
	public AConduitNoMoreInvasion()
	{
		Name = "A Conduit No More (Invasion)";
		QuestId.Add(46205);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		var p = new Vector3(-1155.215, 6200.945, 69.06071, "None");
		if (!ObjectManager.Me.PlayerUsingVehicle)
			return GoToTask.ToPositionAndIntecractWithNpc(p, 117774, 1);

		var npcIDs = new List<int>() {
			117767,
			118069,
			117797,
			106873,
			120319,
			117907,
		};

		var npc = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(npcIDs));
		if (npc != null && npc.IsValid && npc.IsAlive)
		{
			if (npc.GetDistance < 100)
			{
				Lua.RunMacroText("/click OverrideActionBarButton1");
				Thread.Sleep(Usefuls.Latency);
				ClickOnTerrain.Pulse(npc.Position);
				Usefuls.WaitIsCasting();
				return true;
			}
		}
		return true;
	}
	public override bool CanConditions()
	{
		return Quest.GetQuestCompleted(45795);
	}

	public override bool IsComplete()
	{
		return  Quest.IsObjectiveComplete(2, 46205); ;
	}
}

var campPosition = new Vector3(1305.296, 6098.453, 87.61245, "None");
