return true;

uint stoneBuff = 211392;
var stoneObject = 249771;
var stoneNpc = 106621;
var tunnelNpc = 106625;
var caveCenter = new Vector3(872.5681, 6533.303, 52.42089, "None");
//uint dropStoneSpellId = 211441;
if (ObjectManager.Me.Position.DistanceTo2D(caveCenter) > 300)
{
	//Logging.Write("too far from cave. moving there");
	GoToTask.ToPosition(caveCenter);
	return true;
}
if (ObjectManager.Me.HaveBuff(stoneBuff))
{
	wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
	var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(tunnelNpc));
	if (mobEntry != null && mobEntry.IsAlive && mobEntry.IsValid)
	{
		if (ObjectManager.Me.Position.DistanceTo2D(mobEntry.Position) < 3)
		{
			//Logging.Write("have stone and tunnel close. drop it");
			MovementManager.Face(mobEntry);
			Lua.LuaDoString("ExtraActionButton1:Click()");
		}
		else
		{
			//Logging.Write("have stone but tunnel far. getting closer");
			var wp = PathFinder.FindPath(mobEntry.Position);
			MovementManager.Go(wp);
			Thread.Sleep(3 * 1000);
		}
	}
	else
	{
		//Logging.Write("have stone. no tunnels around. waiting");
	}
	return true;
}
else
{
	var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(stoneNpc));
	if (mobEntry != null && mobEntry.IsValid)
	{
		//Logging.Write("no stone. but stone-npc near. interacting");
		GoToTask.ToPositionAndIntecractWithNpc(mobEntry.Position, stoneNpc);
		Thread.Sleep(3 * 1000);
		wManager.wManagerSetting.AddBlackList(mobEntry.Guid, 1000 * 5 * 60); // BlackList mob 5 min
	}
	else
	{
		//Logging.Write("no stone. but stone-object near. interacting");
		wManager.Wow.Bot.Tasks.GoToTask.ToPositionAndIntecractWithGameObject(caveCenter, stoneObject);
		Thread.Sleep(3 * 1000);
	}
}
return true;

