public class SuppliesNeededStormscalesTurnIn : QuestClass
{
	int _questID = 41327;
	public SuppliesNeededStormscalesTurnIn()
	{
		Name = "Supplies Needed: Stormscales (Turn In)";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 40 });
	}
	public override bool CanConditions()
	{
		return WorldQuest.CompleteSkinning(_questID, 1);
	}
	public override bool HasQuest()
	{
		return CanConditions();
	}
	public override bool IsComplete()
	{
		return WorldQuest.CompleteSkinning(_questID);
	}
	public override bool IsCompleted()
	{
		return !CanConditions();
	}
	public override bool Pulse()
	{
		return TurnIn();
	}
}

