public class BattleforAzsunaInvasion : QuestClass
{
	public BattleforAzsunaInvasion()
	{
		Name = "Battle for Azsuna (Invasion)";
		QuestId.Add(46199);
		Step.AddRange(new[] { 1, 1, 1 });
	}
	public override bool Pulse()
	{
		var p = new Vector3(863.8768, 5983.534, 140.5596);
		if (GoToTask.ToPositionAndIntecractWithNpc(p, 119002, 1))
		{
			if (WorldQuest.Scenario.IsButtonEnterPressed)
			{

			}
			Thread.Sleep(Others.Random(10, 15) * 1000);
			return true;
		}
		return true;
	}
	public override bool CanConditions()
	{
		return Quest.GetQuestCompleted(46199);
	}
	public override bool IsCompleted()
	{
		if (Quest.HasQuest(46199))
			return false;
		return base.IsCompleted();
	}
	public override bool IsComplete()
	{
		return Quest.IsObjectiveComplete(2, 46199) || Usefuls.ContinentId == (int)ContinentId.LegionShipHorizontalAzsuna;
	}
}

