	public BattleforAzsunaInvasion5()
	{
		Name = "Battle for Azsuna (Invasion) 5";
		QuestId.Add(46199);
		Step.AddRange(new[] { 1, 1 });
	}
	public override bool Pulse()
	{
		//on ship
		if (ObjectManager.Me.Position.Z - 5f >= _shipPath[0].Z)
		{
			var end = _shipPath[_shipPath.Count - 1];
			var myPos = ObjectManager.Me.Position;
			if (myPos.DistanceTo2D(end) > 20 || myPos.DistanceZ(end) > 10)
			{
				WorldQuest.MovePathFromNear(_shipPath, false);
				return true;
			}
			var stage = WorldQuest.Scenario.Stage;
			if (stage == 2 || stage == 3 || stage == 4 || stage == 5)
			{
				WorldQuest.TryKillElite(end, new List<int>() { _bossID });
				return true;
			}
			else
			{
				if (GoToTask.ToPositionAndIntecractWithNpc(_mountPosition2, _mountID2, 1))
				{
					WorldQuest.CancelCutscene();
				}
				return true;
			}
		}
		if (ObjectManager.Me.PlayerUsingVehicle)
		{
			return true;
		}
		GoToTask.ToPositionAndIntecractWithNpc(_mountPosition, _mountID, 1);
		return true;
	}
	public override bool IsComplete()
	{
		return Usefuls.ContinentId != (int)ContinentId.LegionShipHorizontalAzsuna;
	}
}
