public class BattleforHighmountainInvasion : QuestClass
{
	public BattleforHighmountainInvasion()
	{
		Name = "Battle for Highmountain (Invasion)";
		QuestId.Add(46182);
		Step.AddRange(new[] { 1, 1, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		var p = new Vector3(3930.502, 4144.286, 887.2411);
		if (GoToTask.ToPositionAndIntecractWithNpc(p, 119676, 1))
		{
			//Queue for Scenario
		}
		return true;
	}
	public override bool CanConditions()
	{
		return Quest.GetQuestCompleted(45572);
	}
	public override bool IsCompleted()
	{
		if (Quest.HasQuest(46182))
			return false;
		return base.IsCompleted();
	}
	public override bool IsComplete()
	{
		return Quest.IsObjectiveComplete(2, 46182);
	}
}

