var questId = 42173;
var startPosition = new Vector3(3014.686, 2180.508, 203.7742, "None");
var mobs = new List<int>() {
	97091, //wolf
	107758, //moose
	107755, //moose
};
var maxDist = 10;

if (!Quest.HasQuest(questId))
{
	//Logging.Write("go to start");
	wManager.wManagerSetting.ClearBlacklistOfCurrentProductSession();
	GoToTask.ToPosition(startPosition);
	return true;
}

var corpse = wManager.Wow.ObjectManager.ObjectManager.GetObjectWoWUnit()
	.Where(u => u != null && u.IsValid && u.IsDead && mobs.Contains(u.Entry) && !wManager.wManagerSetting.IsBlackListed(u.Guid) )
	.OrderBy(u => u.Position.DistanceTo(wManager.Wow.ObjectManager.ObjectManager.Me.Position))
	.FirstOrDefault();

if (corpse != null)
{
	if (ObjectManager.Me.Target != corpse.Guid)
	{
		Interact.InteractGameObject(corpse.GetBaseAddress);
	}

	//ObjectManager.Me.Target = corpse.Guid;
	MovementManager.Face(corpse.Position);
	if (ObjectManager.Me.Position.DistanceTo(corpse.Position) > maxDist)
	{
		//Logging.Write("found corpse, get closer");
		GoToTask.ToPosition(corpse.Position);
	}
	else
	{
		//Logging.Write("found corpse, feed");
		MovementManager.StopMove();
		Move.JumpOrAscend(Move.MoveAction.PressKey, 250); // jump
		wManager.Wow.Helpers.Lua.LuaDoString("ExtraActionButton1:Click()");
		Thread.Sleep(Usefuls.Latency * 2);
		wManager.Wow.Helpers.Usefuls.WaitIsCasting();
		wManager.wManagerSetting.AddBlackList(corpse.Guid, 1000 * 3 * 60);
	}
	return true;
}

var mobEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));

if (mobEntry != null && mobEntry.IsValid && mobEntry.IsAlive && mobEntry.IsAttackable)
{
	//Logging.Write("attack target");
	Interact.InteractGameObject(mobEntry.GetBaseAddress);
	Fight.StartFight(mobEntry.Guid);
	return true;
}
//Logging.Write("no target. waiting");
return true;

