public class TheStormsFury2Invasion : QuestClass
{
	Vector3 _startPosition = new Vector3(4744.417, -189.8108, 16.9176, "None");
	int _vethirID = 116868;
	List<int> _mobs = new List<int>()
	{
		116882,
		116850,
		116883,
		116884,
		116899,
		116901,
		116898,
		116909,
		116917,
		116892,
		116885,
		116897,
		116920,
		118566,
	};
	uint _wrathfulStrikeSpellID = 232858;
	string lua = @"start, duration, enabled = GetSpellCooldown('{0}'); return start;";
	public TheStormsFury2Invasion()
	{
		Name = "The Storm's Fury 2 (Invasion)";
		QuestId.Add(45406);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
		lua = string.Format(lua, _wrathfulStrikeSpellID);
	}
	public override bool Pulse()
	{
		if (!ObjectManager.Me.PlayerUsingVehicle)
			return GoToTask.ToPositionAndIntecractWithNpc(_startPosition, _vethirID, 1);

		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_mobs));
		if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable && mob.GetDistance < 195)
		{
			if (Lua.LuaDoString<int>(lua) == 0)
			{
				Lua.RunMacroText("/click OverrideActionBarButton1");
				Thread.Sleep(Usefuls.Latency);
				ClickOnTerrain.Pulse(mob.Position);
				Usefuls.WaitIsCasting();
			}
		}
		return true;
	}
	public override bool IsComplete()
	{
		return Quest.IsObjectiveComplete(2, 45406);
	}
}
