public class HoldingtheRamparts4Invasion : QuestClass
{
	static int _questID = 44789;
	public HoldingtheRamparts4Invasion()
	{
		Name = "Holding the Ramparts 4 (Invasion)";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return Quest.IsObjectiveComplete(4, _questID);
	}
	public override bool Pulse()
	{
		var p = new Vector3(3243.06, 7369.178, 292.173, "None");
		var harvesterID = 115659;
		var generalID = 115603;
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(generalID));
		if (mob != null && mob.IsValid && mob.HealthPercent < 100)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Fight.StartFight(mob.Guid);
			return true;
		}
		var harvester = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(harvesterID));
		if (harvester != null && harvester.IsValid && harvester.IsAlive)
		{
			GoToTask.ToPositionAndIntecractWithNpc(harvester.Position, harvester.Entry);
			return true;
		}
		GoToTask.ToPosition(p);
		return true;
	}
}

