var eggId = 96932;
var eggEntry = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(eggId));
if (eggEntry != null && eggEntry.IsValid)
{
	if (eggEntry.GetDistance < 5)
	{
		MovementManager.MoveTo(eggEntry.Position);
	}
	else
	{
		var path = PathFinder.FindPath(eggEntry.Position);
		MovementManager.Go(path);
	}
}
return true;

