public class AGiantProblem1Group : QuestClass
{
	protected int _questID = 45473;
	protected Vector3 startPosition = new Vector3(-1820.049, 2778.26, -2.197452, "Swimming");

	public AGiantProblem1Group()
	{
		Name = "A Giant Problem 1 (Group)";
		QuestId.Add(_questID);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool IsComplete()
	{
		return WorldQuest.CompleteGroup(_questID, 1);
	}
	public override bool Pulse()
	{
		if (!Quest.HasQuest(_questID))
		{
			if (GoToTask.ToPosition(startPosition))
			{
				MountTask.DismountMount();
			}
			return true;
		}
		if (BrokenShoreHelper.DivingHelmet.Get())
			return true;

		var mobs = new List<int>() {
			118223, // http://www.wowhead.com/npc=118223/sea-giant-pillager
		};
		WorldQuest.TryKillElite(startPosition, mobs);
		return true;
	}
	public override bool CanConditions()
	{
		return WorldQuest.Can(this);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
}

