public class BehindEnemyPortals : QuestClass
{
	Vector3 _portPosition1 = new Vector3(-1433.822, 3018.01, 36.48209, "None");
	Vector3 _portPosition2 = new Vector3(-1425.365, 3029.871, 34.54687, "None");
	Vector3 _campPosition = new Vector3(-1430.697, 2979.659, 415.9702, "None");
	List<int> _mobs = new List<int>() {
		117304, // http://www.wowhead.com/npc=117304/pit-commander-galvinoth
	};

	public BehindEnemyPortals()
	{
		Name = "Behind Enemy Portals";
		QuestId.Add(45559);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	protected virtual bool IsBehindPortal
	{
		get
		{
			return ObjectManager.Me.Position.DistanceZ(_campPosition) < 20;
		}
	}
	protected virtual bool UsePortal()
	{
		if (IsBehindPortal)
			return false;

		if (ObjectManager.Me.Position.DistanceTo(_portPosition1) < 5)
		{
			ClickToMove.CGPlayer_C__ClickToMove(_portPosition2.X, _portPosition2.Y, _portPosition2.Z, MemoryRobot.Int128.Zero(), (int)ClickToMoveType.Move, 0.5f);
			Thread.Sleep(5 * 1000);
			return true;
		}
		GoToTask.ToPosition(_portPosition1);
		return true;
	}
	public override bool Pulse()
	{
		if (IsBehindPortal)
			Execute();
		else
			UsePortal();

		return true;
	}

	protected virtual void Execute()
	{
		WorldQuest.TryKillElite(_campPosition, _mobs);
	}

	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return IsComplete();
	}

	bool _spider()
	{
		var portPosition1 = new Vector3(-1427.06, 3028.228, 34.97655, "None");
		var portPosition2 = new Vector3(-1431.552, 3022.464, 35.92881, "None");
		var campPosition = new Vector3(-1430.697, 2979.659, 415.9702, "None");
		if (ObjectManager.Me.Position.DistanceZ(campPosition) > 10)
		{
			if (ObjectManager.Me.Position.DistanceTo(portPosition1) < 5)
				GoToTask.ToPosition(portPosition2);
			else
				GoToTask.ToPosition(portPosition1);
			return true;
		}
		var mobs = new List<int>() {
		};
		WorldQuest.TryKillAny(campPosition, mobs, 100);
		return true;

	}
}

