public class BruteWrangling : QuestClass
{
	List<int> _mobs = new List<int>() {
			118679,
			118680,
			118683,
	};
	List<Vector3> _hotspots = new List<Vector3>() {
		new Vector3(-740.4254, 2133.625, 0.9562644, "None"),
		new Vector3(-779.879, 2093.094, 0.7724406, "None"),
		new Vector3(-664.9114, 2130.116, 0.7571919, "None"),
		new Vector3(-706.9637, 2061.778, 0.8701078, "None"),
		new Vector3(-678.9153, 1958.109, 0.1634803, "None"),
		new Vector3(-781.9077, 1951.583, 1.878035, "None"),
	};
	public BruteWrangling()
	{
		Name = "Brute Wrangling";
		QuestId.Add(46068);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		if (TryBecomeBiker())
			return true;

		if (TryCombat())
			return true;

		if (FindMob())
			return true;

		GoToTask.ToPosition(_hotspots[Others.Random(0, _hotspots.Count-1)], 3.5f, false, (c) => {
			if (FindMob())
				return false;
			return true;
		});
		return true;
	}
	public static bool TryCombat()
	{
		var target = ObjectManager.GetUnitAttackPlayer().FirstOrDefault();
		if (target == null)
			target = ObjectManager.Me.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;

		Interact.InteractGameObject(target.GetBaseAddress);
		var dist = ObjectManager.Me.Position.DistanceTo(target.Position);
		if (dist > 39)
		{
			var result = false;
			var path = PathFinder.FindPath(target.Position, out result);
			if (result)
				MovementManager.Go(path);

			Thread.Sleep(1 * 1000);
			return true;
		}
		MovementManager.StopMove();

		var lua = @"start, duration, enabled = GetSpellCooldown('{0}'); return start;";

		//charge
		if (Lua.LuaDoString<int>(string.Format(lua, 235585)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton3");
			Usefuls.WaitIsCasting();
			return true;
		}

		if (dist < 15 && Lua.LuaDoString<int>(string.Format(lua, 235581)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton2");
			Usefuls.WaitIsCasting();
			return true;
		}

		if (dist < 15 && Lua.LuaDoString<int>(string.Format(lua, 235583)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
			Usefuls.WaitIsCasting();
			return true;
		}
		return false;
	}
	bool TryBecomeBiker()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
			return false;

		var p = new Vector3(-858.9236, 2159.036, 0.9277518, "None");
		GoToTask.ToPositionAndIntecractWithNpc(p, 120216, 1);
		return true;
	}
	bool FindMob()
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(_mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			return true;
		}
		return false;
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
}

