		new Vector3(-1327.217f, 2650.28f, 41.36594f, "None"),
		new Vector3(-1333.863f, 2642.238f, 42.16872f, "None"),
	};
	public ByWaterBePurged()
	{
		Name = "By Water Be Purged";
		QuestId.Add(46201);
		Step.AddRange(new[] { 0, 0, 0, 0, 0 });
	}
	public override bool CanConditions()
	{
		return true;
	}
	public override bool HasQuest()
	{
		return WorldQuest.Has(this);
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool Pulse()
	{
		if (TryBecomeBiker())
			return true;

		if (TryCombat())
			return true;

		TryFind(_mobs, _path);
		return true;
	}

	bool TryBecomeBiker()
	{
		if (ObjectManager.Me.PlayerUsingVehicle)
			return false;

		var p = new Vector3(-1463.297, 2472.944, 1.250999, "None");
		GoToTask.ToPositionAndIntecractWithNpc(p, 120085, 1);
		return true;
	}

	public static bool TryFind(List<int> mobs, List<Vector3> path)
	{
		var mob = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
		if (mob != null && mob.IsAlive && mob.IsAttackable && mob.IsValid)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Thread.Sleep(Usefuls.Latency + 150);
			return true;
		}
		if (!MovementManager.InMovementLoop)
		{
			var closest = path.OrderBy(v => ObjectManager.Me.Position.DistanceTo(v)).FirstOrDefault();
			GoToTask.ToPosition(closest);
			MovementManager.GoLoop(path);
			return true;
		}
		return true;
	}
	public static bool TryCombat()
	{
		var target = ObjectManager.GetUnitAttackPlayer().FirstOrDefault();
		if (target == null)
			target = ObjectManager.Me.TargetObject;

		if (target == null || !target.IsAlive || !target.IsAttackable || !target.IsValid)
			return false;

		Interact.InteractGameObject(target.GetBaseAddress);
		var dist = ObjectManager.Me.Position.DistanceTo(target.Position);
		if (dist > 55)
		{
			var result = false;
			var path = PathFinder.FindPath(target.Position, out result);
			if (result)
				MovementManager.Go(path);

			Thread.Sleep(1 * 1000);
			return true;
		}


		MovementManager.StopMove();

		var lua = @"start, duration, enabled = GetSpellCooldown('{0}'); return start;";

		uint spellFilterFel = 239331;
		if (ObjectManager.Pet.HealthPercent < 50 && !ObjectManager.Pet.InCombat && Lua.LuaDoString<int>(string.Format(lua, spellFilterFel)) == 0)
		{
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton5");
			Usefuls.WaitIsCasting();
			return true;
		}

		uint spellThrowGlobule = 239277;
		if (dist < 55 && Lua.LuaDoString<int>(string.Format(lua, spellThrowGlobule)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton2");
			Thread.Sleep(Usefuls.Latency * 2);
			ClickOnTerrain.Pulse(target.Position);
			Usefuls.WaitIsCasting();
			return true;
		}

		uint spellRoaringTorrent = 239277;
		if (dist < 30 && Lua.LuaDoString<int>(string.Format(lua, spellRoaringTorrent)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton3");
			Usefuls.WaitIsCasting();
			return true;
		}

		uint spellDrenchingPunch = 239310;
		if (dist < 5 && Lua.LuaDoString<int>(string.Format(lua, spellDrenchingPunch)) == 0)
		{
			MovementManager.Face(ObjectManager.Target);
			Lua.RunMacroText("/click [overridebar][vehicleui][possessbar,@vehicle,exists]OverrideActionBarButton1");
			Usefuls.WaitIsCasting();
			return true;
		}
		return false;
	}

}

