public class OnBorrowedWings2 : QuestClass
{
	public int felGeyserID = 120210;
	public int felEnergyGlobeID = 120214;
	public uint wingsSpellID = 239826;
	public List<Vector3> hotspots = new List<Vector3>()
	{
		new Vector3(-626.8156, 2352.573, 103.1475, "None"),
		//new Vector3(-566.1754, 2248.96, 103.1804),
		//new Vector3(-596.9132, 2301.78, 106.2785),
		//new Vector3(-624.5018, 2324.337, 103.1817),
	};

	public OnBorrowedWings2()
	{
		Name = "On Borrowed Wings 2";
		QuestId.Add(46752);
		Step.AddRange(new[] { 1, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		if (Lua.LuaDoString<bool>("return HasExtraActionBar()") && !ObjectManager.Me.HaveBuff(239826))
		{
			Lua.LuaDoString("ExtraActionButton1:Click()");
		}
		if (ObjectManager.Me.IsFalling)
		{
			var globe = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(felEnergyGlobeID));
			if (globe != null && globe.IsValid)
			{
				var p = globe.Position;
				ClickToMove.CGPlayer_C__ClickToMove(p.X, p.Y, p.Z, MemoryRobot.Int128.Zero(), (int)wManager.Wow.Enums.ClickToMoveType.Move, 1f);
				Thread.Sleep(Others.Random(1000, 2000));
			}
			return true;
		}
		var geyser = ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(felGeyserID));
		if (geyser != null && geyser.IsValid && geyser.GetDistanceZ < 20)
		{
			GoToTask.ToPosition(geyser.Position, 3, false, (c) => {
				if (ObjectManager.Me.IsFalling)
					return false;

				return true;
			});
			return true;
		}
		var randomSpot = hotspots[Others.Random(0, hotspots.Count - 1)];
		if (GoToTask.ToPosition(randomSpot, 10, false, (c) =>
		{
			if (ObjectManager.Me.IsFalling)
				return false;

			return true;
		}))
		{
			MountTask.DismountMount();
		}
		return true;
	}
	public override bool IsComplete()
	{
		return WorldQuest.Complete(this);
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
}

