public class TheBurningKeys : QuestClass
{
	List<Vector3> _hotspots = new List<Vector3>()
	{
		new Vector3(-1098.592, 2675.034, 22.70077, "None"),
		new Vector3(-1170.354, 2702.646, 28.20226, "None"),
		new Vector3(-1264.905, 2654.316, 28.69447, "None"),
	};
	public TheBurningKeys()
	{
		Name = "The Burning Keys";
		QuestId.Add(45805);
		Step.AddRange(new[] { 10, 0, 0, 0, 0 });
	}
	public override bool Pulse()
	{
		if (ItemsManager.HasItemById(143924))
		{
			var cage = Cage();
			if (cage != null && cage.IsValid)
			{
				GoToTask.ToPositionAndIntecractWithGameObject(cage.Position, cage.Entry);
				return true;
			}
			GoToTask.ToPosition(_hotspots[Others.Random(0, _hotspots.Count - 1)], 3.5f, false, (c) => {
				var g = Cage();
				if (g != null && g.IsValid)
					return false;

				return true;
			});
			return true;
		}
		var mob = Mob();
		if (mob != null && mob.IsValid && mob.IsAlive && mob.IsAttackable)
		{
			Interact.InteractGameObject(mob.GetBaseAddress);
			Fight.StartFight(mob.Guid);
			return true;
		}
		GoToTask.ToPosition(_hotspots[Others.Random(0, _hotspots.Count - 1)], 3.5f, false, (c)=> {
			var m = Mob();
			if (m != null && m.IsValid)
				return false;

			return true;
		});
		return true;
	}
	WoWUnit Mob()
	{
		var mobs = new List<int>() { 118059, 118046 };
		return ObjectManager.GetNearestWoWUnit(ObjectManager.GetWoWUnitByEntry(mobs));
