public class WhereThereisaWhip : QuestGrinderClass
{
	Vector3 _portalOutPosition0 = new Vector3(-1376.815, 2986.159, 349.0611, "None");
	Vector3 _portalOutPosition = new Vector3(-1369.996, 2978.419, 349.1501, "None");
	//new Vector3(-1379.751, 2990.96, 314.44, "None")
	// mob
	//
	Vector3 _centerPosition = new Vector3(-1425.056, 3023.623, 323.4818, "None");

	public WhereThereisaWhip()
	{
		Name = "Where There is a Whip...";
		QuestId.Add(45977);
		EntryTarget.AddRange(new[] { 117168, 118441 });
		//HotSpots.Add(_centerPosition);
		HotSpots.Add(new Vector3(-1428.234, 3019.055, 342.8083, "None"));
		HotSpots.Add(new Vector3(-1461.308, 2984.063, 336.6259, "None"));
		HotSpots.Add(new Vector3(-1400.156, 3035.22, 342.7351, "None"));
		Step.AddRange(new[] { 0 });
	}
	public override bool Pulse()
	{
		if (!InShip)
		{
			BrokenShoreHelper.GoToPortal();
			return true;
		}
		if (!WorldQuest.Complete(this, 2))
		{
			return base.Pulse();
		}
		if (!WorldQuest.Complete(this, 3))
		{
			HotSpots.Clear();
			HotSpots.Add(new Vector3(-1392.103, 3063.382, 336.5732, "None"));
			EntryTarget.Clear();
			EntryTarget.Add(117165);
			return base.Pulse();
		}
		return BrokenShoreHelper.GotoPortal(_portalOutPosition0, _portalOutPosition);
	}

	bool InShip
	{
		get
		{
			var myPos = ObjectManager.Me.Position;
			if (myPos.DistanceTo2D(_centerPosition) > 100f)
				return false;

			if (myPos.DistanceZ(_centerPosition) > 30f)
				return false;

			return true;
		}
	}

	public override bool IsComplete()
	{
		return WorldQuest.Complete(this) && !InShip;
	}
	public override bool HasQuest()
	{
		return true;
	}
	public override bool IsCompleted()
	{
		return false;
	}
}
