EasyQuestProfile.Script:
public class Updater
{
public static void Update()
{
robotManager.Helpful.Logging.Write("[Enraged] Checking for updates...");
try
{
    string onlineFile = "https://raw.githubusercontent.com/dglEnraged/horde/master/Horde%5B20-30%5DVanilla(Barrens).xml";
    Quester.Bot.QuesterSetting.Load();
    string currentFile =  System.Windows.Forms.Application.StartupPath + @"\Profiles\Quester\" + wManager.Wow.Helpers.Quest.QuesterCurrentContext.ProfileName;

    var currentFileContent = System.IO.File.ReadAllText(currentFile, System.Text.Encoding.UTF8);
    var onlineFileContent = new System.Net.WebClient {Encoding = System.Text.Encoding.UTF8 }.DownloadString(onlineFile);

    if (!string.IsNullOrWhiteSpace(currentFileContent) && !string.IsNullOrWhiteSpace(onlineFileContent))
    {
        if (currentFileContent.Length == 39 && onlineFileContent.Length == 39) // 39 is size of encrypted files with option "Short file"
        {
            if (currentFileContent != onlineFileContent) // if new update
            {
                robotManager.Helpful.Logging.Write("New version found, try to update file");
                System.IO.File.WriteAllText(currentFile, onlineFileContent, System.Text.Encoding.UTF8); // replace user file by online file
                new System.Threading.Thread(() => robotManager.Products.Products.ProductRestart()).Start(); // reload product (profile)
            }
        }
    }
}
catch (System.Exception e)
{
    robotManager.Helpful.Logging.WriteError("Auto update: " + e);
}﻿﻿﻿﻿
}
}


public class CheckTaskmasterFizzule
{
public static bool TaskmasterFizzulePresent()
{
 var Fizzule = wManager.Wow.ObjectManager.ObjectManager.GetNearestWoWUnit(wManager.Wow.ObjectManager.ObjectManager.GetWoWUnitByEntry(7233));
if (Fizzule.GetDistance <= 50)
{ 
 return true; 
}
return false;
}
}


public class LockPicking
{
public static void LevelPicking()
{
 if ((Quest.HasQuest(2458)) && (wManager.Wow.Helpers.ItemsManager.GetItemCountById(5060) >= 1) && (wManager.Wow.Helpers.Skill.GetValue(wManager.Wow.Enums.SkillLine.Lockpicking) < 75) )
{
var pathstart = new Vector3(-1460.965f, -3869.904f, 14.27123f);

GoToTask.ToPosition(pathstart, 0.1f);
MovementManager.MoveTo(new Vector3(-1460.753 , -3885.383 , 10.43919, "None"));
if (ObjectManager.Me.Position.DistanceTo2D( pathstart ) < 1) {
  var path = new List<Vector3>() {
new Vector3(-1460.753 , -3885.383 , 10.43919, "None"),
new Vector3(-1460.75 , -3888.883 , 10.67112, "None"),
new Vector3(-1460.744 , -3895.337 , 14.32239, "None"),
new Vector3(-1460.741 , -3898.837 , 17.21662, "None"),
new Vector3(-1460.672 , -3905.066 , 18.84221, "None"),
new Vector3(-1460.589 , -3908.453 , 14.53791, "None"),};
MovementManager.Go(path);
}


while ((wManager.Wow.Helpers.ItemsManager.GetItemCountById(5060) >= 1) && (wManager.Wow.Helpers.Skill.GetValue(wManager.Wow.Enums.SkillLine.Lockpicking) < 75))
{
 GoToTask.ToPositionAndIntecractWithGameObject(new Vector3(-1455.359f, -3964.889f, 7.562737f), 123330);
}
}
}
public static void BuyTools()
{
if (Quest.HasQuest(2458) && (wManager.Wow.Helpers.ItemsManager.GetItemCountById(5060) < 1))
{
robotManager.Helpful.Logging.Write("Purchasing Thieves' Tools for Rogue Class Quest");

wManager.wManagerSetting.CurrentSetting.DoNotSellList.Add("Thieves' Tools");

  var npc = new Npc
        {
            Entry = 3334,
            Position = new Vector3(1811.04f, -4270.9f, 7.086275f),
            Type = Npc.NpcType.Vendor
        };
        int itemId = 5060;

        if (GoToTask.ToPositionAndIntecractWith(npc))
        {
            Vendor.BuyItem(ItemsManager.GetNameById(itemId), 1);
        }
}
}
