var zeppelinEntryId = 186238; 
       var fromZeppelinWaitPosition = new Vector3(1185.022, -4144.208, 70.62553); 
       var fromPlayerWaitPosition = new Vector3(1180.093, -4147.332, 51.92663); 
       var fromPlayerInZeppelinPosition = new Vector3(1189.892, -4144.217, 52.78342); 
       var toZeppelinWaitPosition = new Vector3(2837.908, 6187.443, 140.1648);
       var toPlayerLeavePosition = new Vector3(2828.173, 6176.871, 121.9823); 

       wManager.wManagerSetting.CurrentSetting.CloseIfPlayerTeleported = false;


       if (!Conditions.InGameAndConnectedAndProductStartedNotInPause)
           return true;

       if (Usefuls.ContinentId == (int)ContinentId.Kalimdor)
       {
           if (!ObjectManager.Me.InTransport)
           {
               if (GoToTask.ToPosition(fromPlayerWaitPosition))
               {
                   var zeppelin = ObjectManager.GetWoWGameObjectByEntry(zeppelinEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();
                   if (zeppelin != null && zeppelin.Position.DistanceTo(fromZeppelinWaitPosition) < 1)
                   {
                       GoToTask.ToPosition(fromPlayerInZeppelinPosition);
                   }
               }
           }
       }
       else if (Usefuls.ContinentId == (int)ContinentId.Northrend)
       {
           if (ObjectManager.Me.InTransport)
           {
               var zeppelin = ObjectManager.GetWoWGameObjectByEntry(zeppelinEntryId).OrderBy(o => o.GetDistance).FirstOrDefault();
               if (zeppelin != null && zeppelin.Position.DistanceTo(toZeppelinWaitPosition) < 1)
               {
                   Move.Backward(Move.MoveAction.PressKey, 3650);
               }
           }
       }
       return true;

EasyQuestProfile.Script:
using System;
using System.ComponentModel;
using System.IO;
using wManager.Wow.Helpers;
using System.Collections.Generic;
using robotManager.Products;
using robotManager.Helpful;
using System.Linq;
using System.Threading;
using wManager.Wow.Bot.Tasks;
using wManager.Wow.Class;
using wManager.Wow.Enums;
using wManager.Wow.ObjectManager;
using wManager;


public class AutoUpdater
{
    public static void CheckUpdate()
    {
        try
        {
            string onlineFile = "https://github.com/TheSmokie/Horde-First-Aid/raw/master/First%20Aid%20Horde%20(1%20-%20450)";    
            Quester.Bot.QuesterSetting.Load();
            string profileName = "First Aid Horde (1- 450).xml";

            string currentFile = System.Windows.Forms.Application.StartupPath + @"\Profiles\Quester\" + profileName; 
            var currentFileContent = System.IO.File.ReadAllText(currentFile, System.Text.Encoding.UTF8);
            var onlineFileContent = new System.Net.WebClient { Encoding = System.Text.Encoding.UTF8 }.DownloadString(onlineFile);

            if (!string.IsNullOrWhiteSpace(currentFileContent) && !string.IsNullOrWhiteSpace(onlineFileContent))
            {
                    if (currentFileContent != onlineFileContent) // if new update
                    {
                        robotManager.Helpful.Logging.Write("New version found, try to update file", (Logging.LogType)1, System.Drawing.Color.Red);
                        System.IO.File.WriteAllText(currentFile, onlineFileContent); // replace user file by online file
                        new System.Threading.Thread(() => robotManager.Products.Products.ProductRestart()).Start(); // reload product (profile)


                    }
                Logging.Write("[Auto Updater]The version on your pc is the latest updated version.", (Logging.LogType)1, System.Drawing.Color.Red);
            }


        }
        catch (System.Exception e)
        {
            robotManager.Helpful.Logging.WriteError("Auto update: " + e);

        }
    } 
}


public class Extension
{
    public static void Crafty(string profession, string itemName, int quantity)
    {
        if (Usefuls.WowVersion == 12340)
        {
            Lua.LuaDoString(string.Format(@"
                  if not TradeSkillFrame then
                           CastSpellByName('{0}')
                   end

              if TradeSkillFrame:IsVisible() == nil then
                      CastSpellByName('{0}')
                     end 

                     for i=1,GetNumTradeSkills() do
                  local name, _, _, _ = GetTradeSkillInfo(i)
                   if (name == '{1}') then
                  DoTradeSkill(i, {2})
                      end

                  if (TradeSkillFrame:IsVisible() and {2} < 2) then
                  TradeSkillFrame:Hide();
                  end
                      end
               ",
              profession, itemName, quantity));
        }
        else
            if (Usefuls.WowVersion == 8606 || Usefuls.WowVersion == 5875)
        {
            Lua.LuaDoString(string.Format(@"
             if not TradeSkillFrame then
             CastSpellByName('{0}')
             end
            if TradeSkillFrame:IsVisible() == nil then
             CastSpellByName('{0}')
             end 
             for i=1,GetNumTradeSkills() do
            local name, _, _, _ = GetTradeSkillInfo(i)
            if (name == '{1}') then
            DoTradeSkill(i, {2})
            end
            if (TradeSkillFrame:IsVisible() and {2} < 2) then
            TradeSkillFrame:Hide();
            end
            end
        ", profession, itemName, quantity));
        }
    }

    public static bool RecipeIsKnown(string profession, string RecipeName)
    {

        bool Known = Lua.LuaDoString<bool>(string.Format(@"
	    if not TradeSkillFrame then
        CastSpellByName('{0}')
        end

        if TradeSkillFrame:IsVisible() == nil then
        CastSpellByName('{0}')
        end 
            numTradeSkills = GetNumTradeSkills()
            for i = 1, numTradeSkills do
                tradeSkillName, _, _, _ = GetTradeSkillInfo(i)
                if (tradeSkillName == '{1}') then
                    return true;
                end 
	    if (TradeSkillFrame:IsVisible() ) then
            TradeSkillFrame:Hide();
            end
            end
            return false;
        ", profession, RecipeName));

        return Known;
    }

    public static void BuyThisSpell(string skillname)
    {
        Lua.LuaDoString(string.Format(@"
        for i=1,GetNumTrainerServices() do
        local name = GetTrainerServiceInfo(i)
        if (name == '{0}') then
            BuyTrainerService(i)
         end
        end
        ", skillname.Replace("'", "\'")));
    }
}

public class Cloths
{
    public static List<string> Cloth()
    {
        return new List<string>
            {
                "Linen Cloth",
                "Wool Cloth",
                "Silk Cloth",
                "Mageweave Cloth",
                "Runecloth",
                "Felcloth",
                "Mooncloth"
            };

    }
}

public class DoNOtSell
{
    public static void Start()
    {

        foreach (string cloth in Cloths.Cloth())
        {
            if (!wManagerSetting.CurrentSetting.DoNotSellList.Contains(cloth))
            {
                wManagerSetting.CurrentSetting.DoNotSellList.Add(cloth);
                Logging.Write("Do Not Sell List : " + cloth);

            }
        }
    }
}


public class CallingCode
{
    static CallingCode()
    {
        DoNOtSell.Start();

        if (ObjectManager.Me.IsHorde)
        {
            Logging.Write("[First Aid]: Hello, Thank you for using Sye | Smokies 1 - 450 First Aid Profile. Make sure to leave a review and a reaction to help keep profile free. ");

        }
        else
            if (ObjectManager.Me.IsAlliance)
        {
            Products.ProductStop();
            Logging.Write("[First Aid]: This profile is only for Horde ! ");
        }
    }
}


[Serializable]
public class FirstAidHorde : Settings
{

	[Category("REQUIRED [Farming]: ")]
	[DisplayName("Heavy Frostweave Bandage")]
	[Description("Do you wish to farm : [Heavy Frostweave Bandage] Set true or false.")]
	public string HFB { get; set; }


	private FirstAidHorde()
	{
		HFB = null;
	}

	public static FirstAidHorde CurrentSetting { get; set; }


	public bool Save()
	{
		try
		{
			return Save(AdviserFilePathAndName("FirstAidHorde",
				ObjectManager.Me.Name + "." + Usefuls.RealmName));
		}
		catch (Exception e)
		{
			Logging.WriteError("FirstAidHorde > Save(): " + e);
			return false;
		}
	}


	public static bool Load()
	{
		try
		{
			if (File.Exists(AdviserFilePathAndName("FirstAidHorde",
				ObjectManager.Me.Name + "." + Usefuls.RealmName)))
			{
				CurrentSetting = Load<FirstAidHorde>(
					AdviserFilePathAndName("FirstAidHorde",
					ObjectManager.Me.Name + "." + Usefuls.RealmName));
				return true;
			}
			CurrentSetting = new FirstAidHorde();
		}
		catch (Exception e)
		{
			Logging.WriteError("FirstAidHorde > Load(): " + e);
		}
		return false;
	}
}
